function showScrollToTop(){if(!$("#scrollToTopBtn").hasClass("show-scroll-to-top-btn")){$("#scrollToTopBtn").addClass("show-scroll-to-top-btn")}}function hideScrollToTop(){if($("#scrollToTopBtn").hasClass("show-scroll-to-top-btn")){$("#scrollToTopBtn").removeClass("show-scroll-to-top-btn")}}function SrcollToTop(){$("body,html").animate({scrollTop:$("body").offset().top-50},500)}$("document").ready(function(){$(".text-div").elastic();$(".content-div").css("min-height",$(window).height()-130);$(".text-div").css("min-height",$(window).height()-160);$(window).scroll(function(){var scroll=$(this).scrollTop();if(50<scroll){showScrollToTop()}else{hideScrollToTop()}});$("#scrollToTopBtn").click(function(event){event.preventDefault();SrcollToTop()})});var markdownEditor=angular.module("markdownEditor",["hc.marked"]).config(["markedProvider",function(markedProvider){markedProvider.setOptions({gfm:true,tables:true,highlight:function(code){return hljs.highlightAuto(code).value}});markedProvider.setRenderer({link:function(href,title,text){return"<a href='"+href+"' title='"+title+"' target='_blank'>"+text+"</a>"}})}]);markdownEditor.controller("mainCtrl",["$scope","marked",function($scope,marked){$scope.showEditor=true;$scope.showViewer=false;(function(){if(992>=window.innerWidth){$scope.showInTwoCol=false}else{$scope.showInTwoCol=true}$(".text-div").focus()})();$scope.markdownSource="";$scope.$watch("markdownSource",function(){$scope.markdownDisplay=marked($scope.markdownSource)});$scope.editorOn=function(){$scope.showEditor=true;$scope.showViewer=false};$scope.viewerOn=function(){$scope.showEditor=false;$scope.showViewer=true};$(window).resize(function(){$(".content-div").css("min-height",$(window).height()-130);$(".text-div").css("min-height",$(window).height()-160);if(992>=window.innerWidth){if($scope.showInTwoCol){$scope.showInTwoCol=false;$scope.showEditor=true;$scope.showViewer=false}}else{if(!$scope.showInTwoCol){$scope.showInTwoCol=true}}$scope.$apply()})}]);