var markdownEditor=angular.module("markdownEditor",["hc.marked"]).config(["markedProvider",function(markedProvider){markedProvider.setOptions({gfm:true,tables:true,highlight:function(code){return hljs.highlightAuto(code).value}});markedProvider.setRenderer({link:function(href,title,text){return"<a href='"+href+"' title='"+title+"' target='_blank'>"+text+"</a>"}})}]);markdownEditor.controller("mainCtrl",["$scope","marked",function($scope,marked){$scope.showEditor=true;$scope.showViewer=false;(function(){if(992>=window.innerWidth){$scope.showInTwoCol=false}else{$scope.showInTwoCol=true}$("textarea").focus()})();$scope.markdownSource="";$scope.$watch("markdownSource",function(){$scope.markdownDisplay=marked($scope.markdownSource)});$scope.editorOn=function(){$scope.showEditor=true;$scope.showViewer=false};$scope.viewerOn=function(){$scope.showEditor=false;$scope.showViewer=true};$(window).resize(function(){if(992>=window.innerWidth){$scope.showInTwoCol=false;$scope.showEditor=true;$scope.showViewer=false}else{$scope.showInTwoCol=true}$scope.$apply()})}]);