var markdownEditor=angular.module("markdownEditor",["hc.marked"]).config(["markedProvider",function(markedProvider){markedProvider.setOptions({gfm:true,tables:true,highlight:function(code){return hljs.highlightAuto(code).value}});markedProvider.setRenderer({link:function(href,title,text){return"<a href='"+href+"' title='"+title+"' target='_blank'>"+text+"</a>"}})}]);markdownEditor.controller("mainCtrl",["$scope","marked",function($scope,marked){$scope.markdownSource="";$scope.$watch("markdownSource",function(){$scope.markdownDisplay=marked($scope.markdownSource)})}]);